(function ($) {
    "use strict";

    // ======= TOGGLE SIDEBAR (DESKTOP & MOBILE) =======
    $("#sidebarToggle, #sidebarToggleTop").on("click", function (e) {
        if ($(window).width() <= 768) {
            // Mobile: gunakan mobile-open
            $("#accordionSidebar").toggleClass("mobile-open");
            $(".mobile-overlay").toggleClass("show");
        } else {
            // Desktop: gunakan bawaan sb admin
            $("body").toggleClass("sidebar-toggled");
            $(".sidebar").toggleClass("toggled");
            if ($(".sidebar").hasClass("toggled")) {
                $(".sidebar .collapse").collapse("hide");
            }
        }
    });

    // ======= CLOSE SIDEBAR SAAT RESIZE =======
    $(window).resize(function () {
        if ($(window).width() > 768) {
            // Pastikan sidebar desktop kembali normal saat resize ke desktop
            $("#accordionSidebar").removeClass("mobile-open");
            $(".mobile-overlay").removeClass("show");
        } else {
            // Mobile: tutup collapse menu
            $(".sidebar .collapse").collapse("hide");
        }
    });

    // ======= MOBILE OVERLAY =======
    // Buat overlay jika belum ada
    if (!$(".mobile-overlay").length) {
        $("body").append('<div class="mobile-overlay"></div>');
    }

    // Klik overlay = sidebar tertutup
    $(document).on("click", ".mobile-overlay", function () {
        $("#accordionSidebar").removeClass("mobile-open");
        $(".mobile-overlay").removeClass("show");
    });

    // ======= SCROLL TO TOP (Bawaan SB Admin) =======
    $(document).on("scroll", function () {
        var scrollDistance = $(this).scrollTop();
        if (scrollDistance > 100) {
            $(".scroll-to-top").fadeIn();
        } else {
            $(".scroll-to-top").fadeOut();
        }
    });

    $(document).on("click", "a.scroll-to-top", function (e) {
        var $anchor = $(this);
        $("html, body")
            .stop()
            .animate(
                {
                    scrollTop: $($anchor.attr("href")).offset().top,
                },
                1000,
                "easeInOutExpo"
            );
        e.preventDefault();
    });
})(jQuery);
